var Board, Player, sync;

sync = {
  'GDrive': {
    'key': '',
    "scope": "https://www.googleapis.com/auth/drive",
    "app_name": "tetris"
  }
};

Nimbus.Auth.setup(sync);

Board = Nimbus.Model.setup('Board', ['owner', 'pieces', 'current']);

Player = Nimbus.Model.setup('Player', ['id', 'name', 'role', 'online']);

Nimbus.Auth.set_app_ready(function() {
  if (Nimbus.Auth.authorized()) {
    return Player.sync_all(function() {
      return console.log('players synced');
    });
  }
});

$(function() {
  $('#login').click = function() {
    return Nimbus.Auth.authorize('GDrive');
  };
  return $('#invite').click = function() {
    var email;
    email = $('invite_email').val();
    return Nimbus.Client.GDrive.add_share_user_real(email, function(user) {
      var players;
      console.log(user);
      user.online = true;
      players = Player.all();
      if (user.role === 'owner') {
        if (players[0]) {
          return players[0] = user;
        }
      } else if (user.role === 'writer') {
        if (players[1]) {
          return players[1] = user;
        }
      }
    });
  };
});
